mapper.select:
SELECT tc.*
FROM com_comment tc

  tc.target_id = #{targetId}
    AND tc.parent_id IS NULL

    AND tc.enabled = #{enabled}


ORDER BY tc.created_at DESC

SELECT tc.*
FROM com_comment tc
WHERE tc.tree_path REGEXP #{regexTreePath}

SELECT tc.*
FROM com_comment tc


    AND tc.target_id = #{ro.targetId}



    AND tc.tree_path LIKE #{rightRegex}


    AND tc.parent_id IS NULL


ORDER BY tc.created_at DESC
