mapper.select:
SELECT mmur.*
FROM mms_message_user_ref mmur


    AND mmur.message_type = #{ro.messageType}


    AND mmur.receiver_user = #{ro.receiverUser}


ORDER BY mmur.created_at desc

SELECT mmur.*
FROM mms_message_user_ref mmur


    AND mmur.message_type = #{ro.messageType}


    AND mmur.receiver_user = #{ro.receiverUser}


    AND mmur.id > #{ro.nextId}


ORDER BY mmur.created_at desc

SELECT MAX(mmur.id) AS nextId, mmur.creator AS userId FROM mms_message_user_ref mmur
AND mmur.message_type = #{ro.messageType}
AND mmur.receiver_user = #{ro.receiverUser}
AND mmur.creator NOT IN (SELECT creator FROM mms_message_user_ref WHERE id >= #{ro.nextId})
group by mmur.creator order by MAX(id) desc
